package controlstatement;

public class Nestedloopfor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			 for(int i=5; i<=5; i--)
			 {
				 for(int j=1; j<=i; j++)
				//or(int j=0; j<i; j++)
				 {
					 System.out.print("*");
				
				 }
				System.out.println();

	}
	}

}
